import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import PNG from '../../Assets/portfolio-3.png';
import './Work.css';

// Import FontAwesome icon library
import { FontAwesomeIcon } from '@fontawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fotawesome/free-solid-svg-icons';

const Work = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const cardStyle = {
    maxWidth: 345,
    backgroundColor: 'rgb(40, 40, 46)',
    color: 'white',
  };

  const textColor = {
    color: 'rgb(255, 255, 102)',
  }

  const nextSlide = () => {
    // Logika untuk menggeser ke slide berikutnya
    setCurrentIndex((prevIndex) => (prevIndex + 1) % totalSlides);
  };

  const prevSlide = () => {
    // Logika untuk menggeser ke slide sebelumnya
    setCurrentIndex((prevIndex) => (prevIndex - 1 + totalSlides) % totalSlides);
  };

  const totalSlides = 6; // Gantilah dengan jumlah total slide Anda

  const renderSlides = () => {
    const slides = [];
    for (let i = 0; i < totalSlides; i++) {
      slides.push(
        <Card
          key={i}
          sx={cardStyle}
          className={`Card ${i === currentIndex ? 'active' : ''}`}
        >
          <CardActionArea>
            <CardMedia
              component="img"
              height="290"
              image={PNG}
              alt="She"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                Kasir
              </Typography>
              <Typography variant="body2" sx={textColor}>
                Project kasir menggunakan framework laravel.
                Mencakup CRUD.
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      );
    }
    return slides;
  };

  return (
    <section id='works'>
      <h1 className='Title'>My Portfolio</h1>
      <div className='Container'>
        <button className="slide-button" onClick={prevSlide}>
          <FontAwesomeIcon icon={faChevronLeft} />
        </button>
        {renderSlides()}
        <button className="slide-button" onClick={nextSlide}>
          <FontAwesomeIcon icon={faChevronRight} />
        </button>
      </div>
    </section>
  );
}

export default Work;
