import React from 'react'
import './Intro.css'
import bg from '../../Assets/GW.jpg'
import btnImg from '../../Assets/hireme.png'
import { Link } from 'react-scroll'
import { Typewriter } from 'react-simple-typewriter';

const Intro = () => {  
  return (
    <section id='intro'>
      <div className='introContent'>
        <span className='hello'>hello,</span>
        <span className='introText'>I'm
          <span className='introName'>
            <Typewriter words={[' Sergio']}
            loop={true}
            cursor
            cursorStyle='_'
            typeSpeed={70}
            deleteSpeed={30}
            delaySpeed={1000}
            />
          </span> <br />
          <Typewriter words={[' Student ', ' Web DEsi ']}
            loop={true}
            cursor
            cursorStyle='_'
            typeSpeed={70}
            deleteSpeed={30}
            delaySpeed={1000}
            />
        </span>

        <p className='introPara'>I am a skilled web designer with experience in creating<br /> vissualy appealing and user friendly website </p>
        <Link><button className='btn'><img src={btnImg} alt='btn' className='btnImg' />Hire Me</button></Link>
      </div>
      <img src={bg} alt='' className='bg' />
    </section>
  )
}

export default Intro
